//程序的作用：通过UI与用户的交互//作者：吴建杰 20102100035//基本的框架包import java.awt.Container;import java.awt.FlowLayout;import javax.swing.JFrame;import javax.swing.JTextArea;import javax.swing.JTextField;import javax.swing.JScrollPane;import javax.swing.JButton;//事件处理包import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class UnicodeTestUI extends JFrame{		//构造函数	public UnicodeTestUI(){		super("UnicodeTest");		Container c = getContentPane();		c.setLayout(new FlowLayout());						final JTextField [] t = { 		new JTextField("输入姓名:",5),		new JTextField(null,11),		new JTextField("输出结果:",5)		};		JButton b = new JButton("确定");		final JTextArea ta = new JTextArea(null,15,30);		JScrollPane sta = new JScrollPane(ta);		t[0].setEditable(false);		t[2].setEditable(false);		b.addActionListener(new ActionListener(){			public void actionPerformed(ActionEvent e){				JButton b = (JButton)e.getSource();				String str = t[1].getText();				char[] ch = str.toCharArray();           //将String类型转化成char 数组类型				String [] s = new String[100];				String stri = '\n'+"===================================="+'\n';				for(int i =0;i < ch.length;i++){					s[0] = "“"+ch[i]+"”字在Unicode编码表的编号是："+(int)ch[i]+'\n';                 //单个char字符进行print处理					s[1] = "    在Unicode编码表的前一个字符是："+(char)((int)ch[i]-1)+'\n';					s[2] = "    在Unicode编码表的后一个字符是："+(char)((int)ch[i]+1)+'\n';					s[3] = "===================================="+'\n';					s[4+i] = s[0]+s[1]+s[2]+s[3];				}				String st = s[4];				for(int i = 5;i < ch.length + 4;i++){					st = st + s[i];				}				st = stri + st  ;				ta.setText(st);				}			}		);		c.add(t[0]);		c.add(t[1]);		c.add(b);		c.add(t[2]);		c.add(sta);			}	//主程序入口	public static void main(String agrs[]){		UnicodeTestUI app = new UnicodeTestUI();		app.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		app.setSize(350,350);		app.setVisible( true );	}		}